const dbConnect_1 = require("../utils/dbConnect");
function fetchDel(table, id) {
    return new Promise((resolve, reject) => {
        dbConnect_1.mongodbCollection((db, client) => {
            db.collection(table).deleteOne({ _id: dbConnect_1.mongodbId(id) }, (err, res) => {
                if (err)
                    reject(err);
                client.close();
                resolve({ data: res });
            });
        });
    });
}
exports.default = fetchDel;
