const dbConnect_1 = require("../utils/dbConnect");
function fetchGet(table, id) {
    return new Promise((resolve, reject) => {
        dbConnect_1.mongodbCollection((db, client) => {
            db.collection(table).findOne({ _id: dbConnect_1.mongodbId(id) }, (err, res) => {
                if (err)
                    reject(err);
                client.close();
                resolve({ data: res });
            });
        });
    });
}
